function isValid(s) {
  const strArr = s.split("");
  const obj = {};
  let changes = 0,
    average;
  let answer = "YES";

  strArr.forEach(element => {
    obj[element] ? obj[element]++ : (obj[element] = 1);
  });
  console.log(obj);
  for (let [key, value] of Object.entries(obj)) {
    if (!average) {
      average = value;
    } else if (value !== average) {
      if (value === 1) changes++;
      else changes += Math.abs(value - average);
    }

    if (changes > 1) {
      answer = "NO";
      return answer;
    }
  }
  return answer;
}

